export const intialState = {
    basket: [],  
};

const reducer = (state, action) => {
    console.log(action);
    switch (action.type) {
        case "Add_to_Basket" :
        /*logic for add items to basket*/
            return {state};
            break;
        case "Remove_from_Basket" :
        /*logic for removing items from basket*/
            return {state};
            break;
        default:
            return state;
    }
}

export default reducer;
