export const intialState = {
    basket: [
        {id: "1234",
        title: "Echo Dot (3rd Gen) - Smart speaker with Alexa - Charcoal",
        price: 3000,
        rating: 4,
        image: "https://images-na.ssl-images-amazon.com/images/I/6182S7MYC2L._AC_UL320_SR320,320_.jpg",
        
    }
    ],
    user: null,
};

const reducer = (state, action) => {
    console.log(action);
    switch (action.type) {
        case "Add_to_Basket" :
        /*logic for add items to basket*/
            return {                
                ...state,
                basket: [...state.basket, action.item],               
            };
           
        case "Remove_from_Basket" :
        /*logic for removing items from basket*/
            let newBasket = [...state.basket];
            const index = state.basket.findIndex((basketItem) => basketItem.id === action.id);

            if (index>=0) {
                //item exist in basket, remove it
                newBasket.splice(index, 1);
                
            } else {
                console.warn(
                    'cannot remove product (id: ${action.id}) as it is not in basket'
                )
            }
        
            return {
                ...state,
                basket: newBasket,
            };
            
        default:
            return state;
    }
}

export default reducer;
