export const intialState = {
    basket: [],
    user: null,
};

const reducer = (state, action) => {
    console.log(action);
    switch (action.type) {
        case "Add_to_Basket" :
        /*logic for add items to basket*/
            return {                
                ...state,
                basket: [...state.basket, action.item]                
            };
           
        case "Remove_from_Basket" :
        /*logic for removing items from basket*/
            return {state};
            
        default:
            return state;
    }
}

export default reducer;
